import React, {Component} from 'react';
import {Container, ListGroup, ListGroupItem, Button} from 'reactstrap';
import {CSSTransition, TransitionGroup} from 'react-transition-group';
import {connect} from 'react-redux';
import {getItems, setSelected} from '../actions/candidateActions';
import {getIssues} from '../actions/issueActions';

import CandidateListCard from './CandidateListCard';

class Sidebar extends Component {

    componentDidMount(){
        this.props.getItems();
        this.props.getIssues();
    }

    render() {
        const { candidates } = this.props.candidates;

        return (
            <div style={{direction: 'rtl'}} className="sidebar">
                <div style={{direction: 'ltr'}}>
                    {(candidates ? candidates.sort((a,b)=>(a.polling>b.polling)?-1:1).map((candidate)=>(
                        <CandidateListCard candidate={candidate} issues={this.props.issues.issues} onSelect={this.props.setSelected}/>
                    )):null)}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    candidates: state.candidates,
    issues: state.issues,
})

export default connect(mapStateToProps, {getItems, setSelected, getIssues})(Sidebar);
