import {connect} from 'react-redux';
import React, { Component } from 'react'
import './css/CandidatePanel.css';
import moment from 'moment';
import CandidatePanelCircle from './CandidatePanelCircle';

class CandidatePanel extends Component {

    getRandomIntInclusive = (min, max) => {
        min = Math.ceil(min);
        max = Math.floor(max);
        return Math.floor(Math.random() * (max - min + 1)) + min; 
      }

    render() {

        const candidate = this.props.candidates.candidates ?this.props.candidates.candidates.filter((item)=>item._id === this.props.candidates.selectedCandidateId)[0]:null;

        if(!candidate) return null;

        const sorted = this.props.candidates.candidates.sort((a,b)=>(a.polling>b.polling)?-1:1);

        const rank = sorted.indexOf(candidate) + 1;

        const {name, state, dob, slogan, status, polling, description, image, website, websiteDisplay, partyAffiliation} = candidate;

        const age = moment().diff(dob, 'years');

        const getButtonStyle = () => {
            return {
                backgroundColor: `hsla(${this.getRandomIntInclusive(0,360)},42%,22%,1.0)`
            }
        }

        const circleBg = {
            ...getButtonStyle(),
            minWidth: '90px',
        };

        return (
            <div className="candidatePanel">
                <div className="row">
                    <CandidatePanelCircle style={circleBg}>
                        <span className="circle_polling">{Math.floor(polling)}%</span>
                        <span className="circle_rank">#{rank}</span>
                    </CandidatePanelCircle>
                    <div className="title">
                        <h1 className="name">{name}</h1>
                        <h3 className="slogan">{slogan}</h3>
                    </div>
                </div>
                <div className="row">
                    <div style={getButtonStyle()} className="button">
                        <a className="websiteLink" href={website}>{websiteDisplay}</a>
                    </div>
                    <div style={getButtonStyle()} className="button">
                        Age: {age}
                    </div>
                    <div style={getButtonStyle()} className="button">
                        {partyAffiliation}
                    </div>
                    <div style={getButtonStyle()} className="button">
                        {state}
                    </div>
                </div>
                <div className="row content">
                    <div className="column">
                        <h2 className="heading">Positions</h2>
                    </div>
                    <div className="column about">
                        <h2 className="heading">About</h2>
                        <p className="candidateDescription">{description}</p>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    candidates: state.candidates,
    issues: state.issues,
})

export default connect(mapStateToProps, {})(CandidatePanel);
