import React, { Component } from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import './css/CandidateEditForm.css';

class CandidateEditForm extends Component {

    constructor(props){
        super(props);

        this.state = {
            formValues: {},
        }
    }

    componentDidMount = () => {

        const candidate = this.props.candidates.candidates ?this.props.candidates.candidates.filter((item)=>item._id === this.props.candidates.selectedCandidateId)[0]:null;

        if(!candidate) return null;

        this.setState({
            formValues: {...candidate}
        });

    }

    handleFormChange = (event) => {
        event.persist();
        console.log(event.target.name);
        this.setState((prevState) => ({
            formValues: {
                ...prevState.formValues,
                [event.target.name]: event.target.value,
            }
        }))

    }

    render() {
        return (
            <div className="candidateEditForm">
                <div className="candidateEditForm-content">
                    <h1 class="edit-form-title">Edit Candidate</h1>
                    <form onChange={this.handleFormChange} class="edit-flex-row">
                        <div class="edit-flex-col">
                            <div className="form-item">
                                <label>Name</label>
                                <input type="text" name="name"></input>
                            </div>
                            <div className="form-item">
                                <label>State</label>
                                <input type="text" name="state"></input>
                            </div>
                            <div className="form-item">
                                <label>Birthdate</label>
                                <input type="text" name="dob"></input>
                            </div>
                            <div className="form-item">
                                <label>Slogan</label>
                                <input type="text" name="slogan"></input>
                            </div>
                            <div className="form-item">
                                <label>Polling</label>
                                <input type="text" name="polling"></input>
                            </div>
                            <div className="form-item">
                                <label>Affiliation</label>
                                <input type="text" name="partyAffiliation"></input>
                            </div>
                            <div className="form-item">
                                <label>Website URL</label>
                                <input type="text" name="website"></input>
                            </div>
                            <div className="form-item">
                                <label>Website Name</label>
                                <input type="text" name="websiteDisplay"></input>
                            </div>
                            <div className="form-item">
                                <label>Image Name</label>
                                <input type="text" name="image"></input>
                            </div>
                        </div>
                        <div class="edit-flex-col">
                            <div className="form-item-about">
                                <label>About Candidate</label>
                                <textarea type="text" name="description"></textarea>
                            </div>
                        </div>
                    </form>
                    <Link to="/candidates">Close</Link>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    candidates: state.candidate,
})

export default connect(mapStateToProps, {})(CandidateEditForm);
