import React from 'react'

export default function CandidatePositionCard(props) {

    const styles = {

    }

    //Get Issue
    const issue = props.issues.filter(item=>item._id === props.position.issue)[0];
    if (!issue) return null;

    const get_icon = (status) => {
        switch(status){
            case 'supports':
                return (<i class="far fa-check-circle"></i>)
            case 'opposed': 
                return (<i class="far fa-times-circle"></i>)
            default:
                return(<i class="far fa-question-circle"></i>)
        }
    }

    return (
        <div>
            {issue.name}<br/>
            {props.position.status}
        </div>
    )
}
