import axios from 'axios';

export const getItems = () => dispatch => {
    axios.get('/api/candidates')
    .then(res=>
        dispatch({
            type: 'GET_CANDIDATES',
            payload: res.data,
        }))
}

export const setSelected = (id) => dispatch => {
    dispatch({
        type: 'SET_SELECTED_CANDIDATE',
        payload: id,
    })
}
