import {connect} from 'react-redux';
import React, { Component } from 'react'
import './css/CandidatePanel.css';
import moment from 'moment';

import CandidateTitleCard from './CandidateTitleCard';

class CandidatePanel extends Component {
    render() {

        const candidate = this.props.candidates.candidates ?this.props.candidates.candidates.filter((item)=>item._id === this.props.candidates.selectedCandidateId)[0]:null;

        if(!candidate) return null;

        const {name, state, dob, slogan, status, polling, description, image, website, websiteDisplay, partyAffiliation} = candidate;

        const years = moment().diff(dob, 'years');


        return (
            <div className="candidatePanel">
                <div className="circle_container">
                    <div class="circle">
                        <div class="circle__inner">
                            <div class="circle__wrapper">
                                <div class="circle__content">                    <span>{polling}%</span><br/>
                                   <span>#2</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    candidates: state.candidates,
})

export default connect(mapStateToProps, {})(CandidatePanel);
