import React from 'react'
import './css/CandidatePositionCard.css'

export default function CandidatePositionCard(props) {

    const styles = {

    }

    //Get Issue
    const issue = props.issues.filter(item=>item._id === props.position.issue)[0];
    if (!issue) return null;

    const getIcon = (status) => {
        switch(status){
            case 'supports':
                return (<i class="far fa-check-circle"></i>)
            case 'opposed': 
                return (<i class="far fa-times-circle"></i>)
            default:
                return(<i class="far fa-question-circle"></i>)
        }
    }

    return (
        <div>
            <div className="title-row">
                <div className="position-title">{issue.name}</div>
                <div className="icon">{getIcon(props.position.status)}</div>
            </div>
        </div>
    )
}
