import React, {Component} from 'react';
import {Container, ListGroup, ListGroupItem, Button} from 'reactstrap';
import {CSSTransition, TransitionGroup} from 'react-transition-group';
import {connect} from 'react-redux';
import {getItems, setSelected} from '../actions/candidateActions';
import CandidateListCard from './CandidateListCard';

class Sidebar extends Component {

    componentDidMount(){
        this.props.getItems();
    }

    render() {
        const { candidates } = this.props.candidates;

        const outerScroll = {
            direction: 'rtl',
        }

        const innerScroll = {
            direction: 'ltr',
        }

        return (
            <div style={outerScroll} className="sidebar">
                <div style={innerScroll}>
                    {(candidates ? candidates.sort((a,b)=>(a.polling>b.polling)?-1:1).map((candidate)=>(
                        <CandidateListCard candidate={candidate} onSelect={this.props.setSelected}/>
                    )):null)}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    candidates: state.candidates
})

export default connect(mapStateToProps, {getItems, setSelected})(Sidebar);
