import React, { Component } from 'react'
import './css/CandidatePositionCard.css'



export default class CandidatePositionCard extends Component {

    state = {
        isExpanded: false,
    }

    expandContainer = () => {
        this.setState(prevState=>{
            return {
                isExpanded: true,
            }
        })
    }

    contractContainer = () => {
        this.setState(prevState=>{
            return {
                isExpanded: false,
            }
        })
    }

    render() {
        //Get Issue
        const issue = this.props.issues.filter(item=>item._id === this.props.position.issue)[0];
        if (!issue) return null;

        const getIcon = (status) => {
            switch(status){
                case 'supports':
                    return (<i class="far fa-check-circle"></i>)
                case 'opposed': 
                    return (<i class="far fa-times-circle"></i>)
                default:
                    return(<i class="far fa-question-circle"></i>)
            }
        }

        const getContainerStyle = (status) => {
            switch(status){
                case 'supports':
                    return ({background:'#CEF6E3',color:'#0D472C'})
                case 'opposed': 
                    return ({background:'#F6CECE',color:'#470D0D'})
                default:
                    return ({background:'#F7F1D2',color:'#473D0D'})
            }
        }
        return (
            <div onClick={this.expandContainer}>
                <div className="position-container" style={getContainerStyle(this.props.position.status)}>
                    <div className="title-row">
                        <div className="position-title">{issue.name}</div>
                        <div className="icon">{getIcon(this.props.position.status)}</div>
                    </div>
                    {(this.state.isExpanded ? 
                        <div>
                            <p className="position-description">                
                                {this.props.position.description}
                                {this.props.position.links.map((item, index)=><a href={item.url}>[{index+1}]</a>)}
                            </p>
                            <span class="position-close">
                                <i onClick={this.contractContainer} class="fas fa-times"></i>
                            </span>
                        </div>
                    :null)}
                </div>
            </div>
        )
    }
}
